import React from 'react';
import './App.css';
class Databutton extends React.Component {

    constructor(props) {
        super(props);
        this.activeName = '';
    }
    handleClick = (name) => {
        this.setState({ activeName: name });
        alert(this.activeName);
    }

    render() {
        return (
            <div className="shadowClass">
                <button className={this.activeName === '' ? 'button' : 'button active'} name="button" onClick={(e) => this.handleClick(e.target.name)}>
                    <span class="fa fa-star leftAlign starSize"></span>
                    <span class="centerText"> Do. Or do not. There is no try </span>
                    <span class="fa fa-star rightAlign fa-lg starSize"></span>
                </button>
            </div>
        );
    }
}
export default Databutton;
