import React from 'react';
import './App.css';
class Databutton extends React.Component {

    constructor(props) {
        super(props);
        this.state = { activeName:0 }
    }
    handleClick = (name) => {
        this.setState({ activeName: this.state.activeName +1 });
        alert(this.state.activeName);
    }

    render() {
        return (
            <div className="shadowClass">
                <button className={this.state.activeName === '' ? 'button' : 'button active'} onClick={(e) => this.handleClick(e.target.className)}>
                    <span class="fa fa-star leftAlign starSize"></span>
                    <span class="centerText"> Do. Or do not. There is no try </span>
                    <span class="fa fa-star rightAlign fa-lg starSize"></span>
                </button>
            </div>
        );
    }
}
export default Databutton;
