import React from 'react';
import './App.css';
class Databutton extends React.Component {

    constructor(props) {
        super(props);
        this.state = { activeName:'button' }
    }
    handleClick = (name) => {
        
        if (name == "activeButton")
            this.setState({ activeName: 'button' });
        else
            this.setState({ activeName: 'activeButton' });
        alert(this.state.activeName);
    }

    render() {
        return (
            <div className="shadowClass">
                <button className={this.state.activeName} onClick={(e) => this.handleClick(e.target.className)}>
                    <span class="fa fa-star leftAlign starSize"></span>
                    <span class="centerText"> Do. Or do not. There is no try </span>
                    <span class="fa fa-star rightAlign fa-lg starSize"></span>
                </button>
            </div>
        );
    }
}
export default Databutton;
