import React from 'react';
import './App.css';
class Databutton extends React.Component {

  
    handleClick = () => {
        this.preventDefault();

        const activeName = this.target.name;
        this.setState({ activeName });
    }

    render() {
        return (
            <div className="shadowClass" onClick={(e) => this.handleClick(e)}>
                <button className={this.state.activeName === 'button' ? 'active' : ''}>
                    <span class="fa fa-star leftAlign starSize"></span>
                    <span class="centerText"> Do. Or do not. There is no try </span>
                    <span class="fa fa-star rightAlign fa-lg starSize"></span>
                </button>
            </div>
        );
    }
}
export default Databutton;
