import React from "react";
import "./App.css";
class Databutton extends React.Component {
    constructor(props) {
        super(props);
        this.state = { activeName: "button", click: false }
    }

    //click for assigning class for button and for showing data
    handleClick = () => {
        if (!this.state.click) {
            this.setState({ activeName: "button" });
            this.setState({ click: true });
        }
        else {
            this.setState({ activeName: "activeButton" });
            this.setState({ click: false });
        }
    }

    //Html for button and data
    render() {
        return (
            <div click="App">
                <div className="shadowClass form-control" onClick={(e) => this.handleClick()}>
                    <button className={this.state.activeName}>
                        <span class="fa fa-star leftAlign starSize" />
                        <span class="centerText">Do. Or do not. There is no try</span>
                        <span class="fa fa-star rightAlign starSize" />
                    </button>
                </div>
                <div className={!this.state.click ? "" : "hidden"}>
                    <div className="textClass">
                        Which of all StarWars movies has longest opening crawl ?
                    </div>
                </div>
            </div>
        );
    }
}
export default Databutton;
